function Header(course) {
    return(
      <div>
      <h1>{course.name}</h1>
      </div>
    )
}


function Total(course) {


    const totalParts = course.content.map(element => element.exercises)
                                     .reduce((prevNum, curNum) => prevNum + curNum)

  return(
    <div>
      <p><strong>total of exercises: {totalParts}</strong></p>
    </div>
  )
}

function Content(course) {

  const parts = course.content.map(element => {
    return(
      <Part key={element.exercises} part={element}/>
    )
  })

  return(
    <div>
      {parts}
    </div>
  )
}

function Part({part}) {
  return(
    <div>
    <p>
      {part.name} {part.exercises}
      </p>
    </div>
  )
}

function Course({course}) {
  return(
      <div>
        <Header name={course.name}/>
        <Content content={course.parts}/>
        <Total content={course.parts}/>
      </div>
  )
}

function App() {

  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    },
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  return courses.map(course => <Course id={course.id} course={course}/>)
}

export default App;
